import React from 'react';
import { useState } from 'react';
import socketClient from 'socket.io-client';

import './App.css';
import Modal from './components/Modal';

const SERVER = 'http://192.168.0.115:8080';

const App = () => {
  let [username, setUsername] = useState('');
  let [messages, setMessages] = useState([]);

  const assignUsername = (username) => {
    setUsername(username);
  };

  const addMessageHandler = (message) => {


    setMessages((prevMessages) => {
      return [...prevMessages, message];
    });
  };

  const socket = socketClient(SERVER);

  socket.on('connection', () => {
    console.log(`I'm connected with the back-end`);
  });

  socket.on('message', (res) => {
    if (res.username !== username) {
      addMessageHandler(res, 'sent-by-others');
    }
  });

  const sendMessage = () => {
    addMessageHandler(res, 'sent-by-me');
    socket.emit('message', { username, text: 'Hello' });
  }

  return (
    <div className='App'>
      {!username && <Modal onSuccessHandler={assignUsername} socket={socket}></Modal>}
      <div className='chat'>
        {messages.map((message) => (
          <p>{message.text}</p>
        ))}
        <button onClick={sendMessage}>Enviar</button>
      </div>
    </div>
  );
}

export default App;
