import React, { useEffect } from 'react';
import { useState } from 'react';
import uuid from 'react-uuid'

import './App.css';
import Modal from './components/Modal';
import socketClient from 'socket.io-client';

const SERVER = 'http://192.168.0.115:8080';

const App = () => {
  const [username, setUsername] = useState('');
  const [messages, setMessages] = useState([]);
  const [messageText, setMessageText] = useState('');
  const [socket, setSocket] = useState(null);

  const assignUsername = (username) => {
    setUsername(username);    
  };

  const changeMessageHandler = (event) => {
    setMessageText(event.target.value);
  }

  const addMessageHandler = (message, className) => {
    setMessages((prevMessages) => {
      return [...prevMessages, { ...message, key: uuid(), className}];
    });
  };

  const addExternalMessage = (messageReceived) => {
    if (messageReceived.username === username) {
      return;
    }

    addMessageHandler(messageReceived, 'sent-by-others');
  }

  useEffect(() => {
    const newSocket = socketClient(SERVER);
    newSocket.on('connection', (socket) => {
      console.log('socket connected', socket);
    });

    newSocket.on('message', (res) => {
      addExternalMessage(res);
    });

    setSocket(newSocket);
    return () => newSocket.close();
  }, [username]); 

  const sendMessage = () => {
    const message = { author: username, text: messageText };
    socket.emit('message', message);
    addMessageHandler(message, 'sent-by-me');
    setMessageText('');
  }

  return (
    <div className='App'>
      {!username && <Modal onSuccessHandler={assignUsername} socket={socket}></Modal>}
      <div className='chat'>
        {messages.map((message) => (
          <p className={message.className} key={message.key}>{message.text}</p>
        ))}
        <input value={messageText} onChange={changeMessageHandler}></input>
        <button onClick={sendMessage}>Enviar</button>
      </div>
    </div>
  );
}

export default App;
